import phrasesFriends from '../data/phrases.json';
import { useState } from 'react';
import '../styles/App.scss';

function App() {
  return (
    <>
      <header>
        <h1>Frases de Friends.</h1>
      </header>

      <main>
        {/* <ul>{htmlData}</ul> */}
        <form className="new-contact__form">
          <h2>Añade un nuevo contacto</h2>
          <input
            className="new-contact__input"
            type="text"
            name="name"
            id="name"
            placeholder="Nombre"
            onChange={handleNewContact}
            value={newContact.name}
          />
          <input
            className="new-contact__input"
            type="text"
            name="lastname"
            id="lastname"
            placeholder="Apellidos"
            onChange={handleNewContact}
            value={newContact.lastname}
          />
          <input
            className="new-contact__input"
            type="phone"
            name="phone"
            id="phone"
            placeholder="Teléfono"
            onChange={handleNewContact}
            value={newContact.phone}
          />
          <input
            className="new-contact__input"
            type="email"
            name="email"
            id="email"
            placeholder="Email"
            onChange={handleNewContact}
            value={newContact.email}
          />
          <input
            className="new-contact__btn"
            type="submit"
            value="Añadir"
            onClick={handleClick}
          />
        </form>
      </main>
    </>
  );
}

export default App;
