import phrasesFriends from '../data/phrases.json';
import { useState } from 'react';
import '../styles/App.scss';

function App() {
  const [data, setData] = useState(phrasesFriends);

  const htmlData = data
    .filter(
      (contact) =>
        contact.name.toLowerCase().includes(search.toLowerCase()) ||
        contact.lastname.toLowerCase().includes(search.toLowerCase())
    )
    .map((contact, i) => {
      return (
        <li key={i}>
          <p>
            <label>Nombre:</label>
            {contact.name} {contact.lastname}
          </p>
          <p className="contact__phone">
            <label className="contact__label">Teléfono:</label>
            <a
              href={`tel:${contact.phone}`}
              title="Pulsa aquí para llamar a Lola"
            >
              {contact.phone}
            </a>
          </p>
          <p className="contact__mail">
            <label className="contact__label">Email:</label>
            <a
              href="mailto:lmartinez@adalab.es"
              title="Pulsa aquí para escribir a Lola"
            >
              {contact.email}
            </a>
          </p>
        </li>
      );
    });

  return (
    <>
      <header>
        <h1>Frases de Friends.</h1>
      </header>

      <main>
        <ul>{htmlData}</ul>
        <form>
          <h2>Añade una nueva frase</h2>
          <label htmlFor="pharse">Frase</label>
          <input type="text" name="phrase" id="phrase" />

          <label htmlFor="character">Personaje</label>
          <input type="text" name="character" id="character" />

          <input
            className="new-contact__btn"
            type="submit"
            value="Añadir una nueva frase"
          />
        </form>
      </main>
    </>
  );
}

export default App;
